import React, { useState } from 'react'
import './customphoneinput.css'

import { Menu, Dropdown } from 'antd';
import { CaretDownOutlined, DownOutlined } from '@ant-design/icons';
import { getCountryArray } from '../../constants/country';

function CustomPhoneInput({ onChange, onBlur, name, value }) {

    let [selecedCountry, selectCountry] = useState({ "name": "India", "flag": "ðŸ‡®ðŸ‡³", "code": "IN", "dial_code": "+91" })

    const menu = (
        <Menu getPopupContainer={triggerNode => triggerNode.parentNode}>

            {
                getCountryArray().length > 0 ?
                    getCountryArray().map((eachCountry, key) => {

                        return (
                            <Menu.Item key={key} onClick={() => { selectCountry(eachCountry) }}>

                                <div>{eachCountry.flag} {eachCountry.name}</div>

                            </Menu.Item>
                        )



                    })


                    : null
            }

        </Menu >
    );


    let handleOnChangeNumber = (e) => {


        let readValue=e.target.value

        let result = {
            value: `${selecedCountry?.dial_code.replace(/\+/g, ' ')}${readValue}`,
            data: {
                "name": selecedCountry?.name,
                "dialCode": selecedCountry?.dial_code,
                "countryCode": selecedCountry?.code.replace(/\+/g, ' '),
                "format": "+.. .....-....."
            },
            e,
            formattedValue: `${selecedCountry?.dial_code}${readValue}`
        }

        onChange(result.value, result.data, result.e, result.formattedValue )


    }


    return (

        <div className='custom_phone_input_container'>

            <div className="flag_box " getPopupContainer={triggerNode => triggerNode.parentNode}>
                <Dropdown getPopupContainer={triggerNode => triggerNode.parentNode} overlay={menu} trigger={['click']}>
                    <a className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                        <CaretDownOutlined />  <img className='flag_image' src={`https://countryflagsapi.com/png/${selecedCountry ? selecedCountry.code : "india"}`} alt="" />&nbsp;{selecedCountry ? selecedCountry.dial_code : "+91"}&nbsp;
                    </a>
                </Dropdown>
            </div>

            <div className="number_box">
                <input naem={name} onBlur={onBlur} onChange={handleOnChangeNumber} placeholder='Enter mobile number' type="tel" name="" id="" />
            </div>

        </div>
    )
}

export default CustomPhoneInput
