import React from 'react'
import './customphoneinput.css'

import { Menu, Dropdown } from 'antd';
import { DownOutlined } from '@ant-design/icons';
import { getCountryArray } from '../../constants/country';

function CustomPhoneInput() {

    const menu = (
        <Menu>

            {
                getCountryArray().length > 0 ?
                    getCountryArray().map((eachCountry, key) => {

                        return (
                            <Menu.Item key={key}>

                                   <div>{eachCountry.name}</div>

                            </Menu.Item>
                        )



                    })


                    : null
            }
            <Menu.Item>
                <a target="_blank" rel="noopener noreferrer" href="https://www.antgroup.com">
                    1st menu item
                </a>
            </Menu.Item>
            <Menu.Item icon={<DownOutlined />} disabled>
                <a target="_blank" rel="noopener noreferrer" href="https://www.aliyun.com">
                    2nd menu item (disabled)
                </a>
            </Menu.Item>
            <Menu.Item disabled>
                <a target="_blank" rel="noopener noreferrer" href="https://www.luohanacademy.com">
                    3rd menu item (disabled)
                </a>
            </Menu.Item>
            <Menu.Item danger>a danger item</Menu.Item>
        </Menu >
    );


    return (

        <div className='custom_phone_input_container'>

            <div className="flag_box ">
                <Dropdown overlay={menu} trigger={['click']}>
                    <a className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                        <img className='flag_image' src={`https://countryflagsapi.com/png/${"india"}`} alt="" />&nbsp;+91&nbsp;  <DownOutlined />
                    </a>
                </Dropdown>
            </div>

            <div className="number_box">
                <input placeholder='Enter mobile number' type="tel" name="" id="" />
            </div>

        </div>
    )
}

export default CustomPhoneInput
